function toggleModal(e,t,d){const o=document.querySelectorAll(t),l=document.querySelector(e),n=l.querySelector(d);o.forEach(e=>{e.addEventListener("click",e=>{let t=window.innerWidth-document.body.offsetWidth+"px";e.preventDefault(),l.style.display="flex",document.body.style.overflow="hidden",document.body.style.paddingRight=t})}),n.addEventListener("click",()=>{l.style.display="none",document.body.style.overflow="",document.body.style.paddingRight=""}),l.addEventListener("click",e=>{e.target===l&&n.click()})}toggleModal(".modal",".btn-open",".btn-close");